// Get all stored module IDs from both storage locations
const online = flow.get('online_modules') || {};
const active = global.get('active_modules') || {};

// Collect ALL module IDs (both MOD_ and obsolete)
const allModuleIds = new Set();
Object.keys(online).forEach(id => allModuleIds.add(id));
Object.keys(active).forEach(id => allModuleIds.add(id));

const moduleArray = Array.from(allModuleIds);

if (moduleArray.length === 0) {
    return [{
        notification: 'âœ… No modules found in storage to clear'
    }];
}

// Clear ALL storage completely
flow.set('online_modules', {});
global.set('active_modules', {});
global.set('selected_module_id', null);

// Create clear messages for ALL modules in MQTT broker
const clearMessages = [];
moduleArray.forEach(id => {
    clearMessages.push({
        topic: `cocktail/bottle/${id}/status`,
        payload: '',
        retain: true
    });
    clearMessages.push({
        topic: `cocktail/bottle/${id}/state`,
        payload: '',
        retain: true
    });
});

const notification = {
    notification: `ðŸ§¹ COMPLETE RESET: Cleared ${moduleArray.length} modules from storage and MQTT: ${moduleArray.join(', ')}`
};

node.log(`Complete reset - cleared all modules: ${moduleArray.join(', ')}`);
node.log(`Sending ${clearMessages.length} MQTT clear messages`);

// Return notification first, then all clear messages
const outputs = [notification];
for (let i = 0; i < clearMessages.length && i < 30; i++) {
    outputs.push(clearMessages[i]);
}

return outputs;

// Initialize all storage - using online_modules instead of registered
flow.set('online_modules', flow.get('online_modules') || {});
global.set('active_modules', {});
global.set('selected_module_id', null);
global.set('admin_authenticated', false);

// Initialize pump duration with default value in both places
flow.set('pump_duration', 1000);
global.set('pump_duration', 1000);

// Default network settings
let netSettings = flow.get('network_settings');
if (!netSettings) {
    flow.set('network_settings', {
        home_ssid: '',
        home_pass: '',
        ap_ssid: 'CocktailMachine',
        ap_pass: 'Cocktail2024!',
        mqtt_host: '192.168.50.1',
        mqtt_port: 1883
    });
}

msg.payload = {
    status: 'System initialized - Online Module Tracking',
    timestamp: new Date()
};
return msg;

// Extract module ID from topic (this is the unchangeable ID)
const topicParts = msg.topic.split('/');
const moduleId = topicParts[2];

// Only process modules with proper ID format (MOD_XXXXXX)
if (!moduleId.startsWith('MOD_')) {
    node.warn(`Ignoring obsolete module format: ${moduleId}`);
    return null;
}

let online = flow.get('online_modules') || {};
let active = global.get('active_modules') || {};

// Get both ID and name from the message
const displayName = msg.payload.name || moduleId;
const fixedId = msg.payload.id || moduleId;

// Double-check that the ID matches expected format
if (!fixedId.startsWith('MOD_')) {
    node.warn(`Ignoring module with invalid ID format: ${fixedId}`);
    return null;
}

// Update active module with data (track by fixed ID)
if (!active[fixedId]) {
    active[fixedId] = {
        id: fixedId,
        name: displayName,
        online: true,
        state: msg.payload,
        lastUpdate: new Date(),
        firstSeen: new Date(),
        dataPoints: 1
    };
} else {
    active[fixedId].state = msg.payload;
    active[fixedId].lastUpdate = new Date();
    active[fixedId].online = true;
    active[fixedId].dataPoints = (active[fixedId].dataPoints || 0) + 1;

    // Always update the name (it can change)
    if (displayName) {
        active[fixedId].name = displayName;
        // Update online module name if it exists
        if (online[fixedId]) {
            online[fixedId].name = displayName;
            flow.set('online_modules', online);
        }
    }
}

global.set('active_modules', active);

// Auto-register module if not already online (track by ID)
if (!online[fixedId]) {
    online[fixedId] = {
        id: fixedId,
        name: displayName,
        registered: new Date(),
        auto_registered: true
    };
    flow.set('online_modules', online);
    node.log(`Module ID ${fixedId} (${displayName}) auto-registered`);
}

// Return enhanced message
msg.moduleId = fixedId;
msg.moduleName = displayName;
msg.moduleData = active[fixedId];

return [msg, msg];

const topicParts = msg.topic.split('/');
const moduleId = topicParts[2];
const isOnline = (msg.payload === 'online');

// Only process modules with proper ID format (MOD_XXXXXX)
if (!moduleId.startsWith('MOD_')) {
    node.warn(`Ignoring obsolete module status: ${moduleId}`);
    return null;
}

let online = flow.get('online_modules') || {};
let active = global.get('active_modules') || {};

// Enhanced status tracking
if (!active[moduleId]) {
