return [msg, msg];

const online = flow.get('online_modules') || {};
const active = global.get('active_modules') || {};

let onlineList = 'Online Modules:\n';
let count = 0;
let options = [];

for (const key in online) {
  count++;
  const mod = online[key];
  const am = active[key];

  let status = 'Offline', level = '--', icon = 'ðŸ”´';
  if (am) {
    const last = am.lastUpdate ? new Date(am.lastUpdate) : null;
    const recent = last ? (Date.now() - last.getTime()) < 30000 : false;
    if (am.online && recent) { status = 'Online'; icon = 'ðŸŸ¢'; }
    else if (am.online && !recent) { status = 'Stale'; icon = 'ðŸŸ¡'; }
    if (am.state?.valid) level = `${am.state.pct}%`;
  }

  const name = mod?.name || key;
  const displayText = `${name} (${key})`; // Show name and ID
  onlineList += `${count}. ${displayText} - ${level} - ${status}\n`;
  options.push({ label: `${icon} ${displayText} - ${level}`, value: key }); // value is the ID
}

if (count === 0) {
  onlineList += 'No online modules';
  options = [{ label: 'No modules online', value: '' }];
}

// Send both msg.options and msg.ui_control.options
return [
  { payload: '', options, ui_control: { replace: true, options } }
];

if (msg.payload && msg.payload !== '') {
