return [
    {payload: statusText, color: statusColor},
];

// Build the GitHub RAW URL and auth header (for private repos)
const owner  = (env.get('GITHUB_OWNER') || 'sebastienlepoder');
const repo   = (env.get('DEPLOY_REPO') || 'cocktail-deploy');
const branch = (env.get('BRANCH') || 'main');
const token  = (env.get('RAW_GITHUB_TOKEN') || '');

msg.method = 'GET';
msg.url = `https://raw.githubusercontent.com/${owner}/${repo}/${branch}/web/versions.json`;
msg.headers = token ? { Authorization: `token ${token}` } : {};
msg.topic = 'versions';
return msg;

const d = msg.payload || {};
const versions = d.versions || {};
const current = (d.current || '').toString().trim() || 'v0.0.0';

function parse(v){ const m=(v||'').replace(/^v/i,'').split('.').map(n=>parseInt(n||0,10)); return {M:m[0]||0,m:m[1]||0,p:m[2]||0}; }
function lt(a,b){ const A=parse(a),B=parse(b); if(A.M!==B.M) return A.M<B.M; if(A.m!==B.m) return A.m<B.m; return A.p<B.p; }

const dash = versions.dashboard || {};
const mods = versions.modules || {};
const back = versions.backend || {};

const model = {
  dashboard: {
    current: current,
    latest:  dash.latest || current,
    notes:   Array.isArray(dash.notes) ? dash.notes : [],
    artifact: dash.artifact || '',
    updateAvailable: lt(current, dash.latest||current)
  },
  modules: {
    latest:  mods.latest || '-',
    notes:   Array.isArray(mods.notes) ? mods.notes : []
  },
  backend: {
    latest:  back.latest || '-',
    notes:   Array.isArray(back.notes) ? back.notes : []
  }
};

flow.set('latest_dashboard_version', model.dashboard.latest);
msg.payload = model;
return msg;

if (msg && msg.action === 'install' && msg.target === 'dashboard') {
  const ver = msg.version || flow.get('latest_dashboard_version');
  node.status({fill:'blue',shape:'dot',text:`Installing ${ver}â€¦`});
  // Exec node expects full command string as payload after -lc
  msg.payload = `\"/opt/scripts/update_dashboard.sh ${ver}\"`;
  return [msg, {payload:`Installing Dashboard ${ver}â€¦` }];
}
return [null, {payload:'No action'}];

// Keep current deployed version
const current = (msg.payload || "").toString().trim();
msg.currentVersion = current || null;

// Build: https://api.github.com/repos/:owner/:repo/contents/web/versions.json?ref=:branch
const owner  = env.get('GITHUB_OWNER') || 'sebastienlepoder';
const repo   = env.get('DEPLOY_REPO')  || 'cocktail-deploy';
const branch = env.get('BRANCH')       || 'main';
msg.url = `https://api.github.com/repos/${owner}/${repo}/contents/web/versions.json?ref=${branch}`;

// Headers
const tok = env.get('RAW_GITHUB_TOKEN') || '';
msg.headers = { 'User-Agent': 'cocktailpi', 'Accept': 'application/vnd.github+json' };
if (tok) msg.headers.Authorization = `Bearer ${tok}`;
return msg;

// Contents API returns base64 content in payload.content
function parseArr(v){
  v = (v||'').toString().trim().replace(/^v/i,'');
  const m = v.match(/(\d+)\.(\d+)\.(\d+)/);
  if(!m) return [0,0,0];
  return [parseInt(m[1]), parseInt(m[2]), parseInt(m[3])];
}
function cmp(a,b){ for(let i=0;i<3;i++){ if(a[i]<b[i]) return -1; if(a[i]>b[i]) return 1; } return 0; }

let latestRaw = null;
try {
  const api = msg.payload || {};
  const text = Buffer.from(api.content || '', api.encoding || 'base64').toString('utf8');
  const json = JSON.parse(text);
  latestRaw = (json.dashboard && json.dashboard.latest) || json.latest || null;
} catch (e) {
  latestRaw = null;
}

const current = msg.currentVersion; // e.g., v2.2.0
let updateAvailable = false;
if (current && latestRaw) updateAvailable = cmp(parseArr(current), parseArr(latestRaw)) < 0;
else if (latestRaw && !current) updateAvailable = true;

msg.headers = {"Access-Control-Allow-Origin":"*"};
msg.payload = { current: current || null, latest: latestRaw || null, updateAvailable };
return msg;

const need = env.get('UPDATE_UI_TOKEN') || '';
const got  = (msg.req && msg.req.headers && msg.req.headers['x-update-token']) || '';
if (need && got !== need) {
    msg.statusCode = 403;
    msg.headers = {"Access-Control-Allow-Origin":"*"};
    msg.payload = { error: 'forbidden' };
    return [null, msg];
}
let body = {};
try { body = typeof msg.payload === 'object' ? msg.payload : JSON.parse(msg.payload || '{}'); } catch(e) {}
const ver = (body.version || 'latest').toString();
msg.payload = `/opt/scripts/update_dashboard.sh ${ver}`;
return msg;

msg.statusCode = 500;
msg.headers = {"Access-Control-Allow-Origin":"*"};
msg.payload = { ok:false, error: (msg.payload||'update failed') };
return msg;

msg.headers = {"Access-Control-Allow-Origin":"*"};
msg.payload = { ok:true, version: (msg.payload||'').toString().trim() };
return msg;

msg.statusCode = 204;
msg.headers = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'Content-Type, X-Update-Token',
  'Access-Control-Allow-Methods': 'POST, OPTIONS'
};
return msg;
