// Process bottle status updates
const payload = JSON.parse(msg.payload);
const moduleId = payload.module_id;

// Store in context for web dashboard
const bottles = flow.get('bottles') || {};
bottles[moduleId] = {
  ...bottles[moduleId],
  status: payload.status,
  wifi_connected: payload.wifi_connected,
  mqtt_connected: payload.mqtt_connected,
  ip_address: payload.ip_address,
  last_seen: new Date().toISOString()
};

flow.set('bottles', bottles);

// Forward to database storage
msg.table = 'bottle_status';
msg.payload = payload;

return msg;

// Process bottle level updates
const payload = JSON.parse(msg.payload);
const moduleId = payload.module_id;

// Store in context
const bottles = flow.get('bottles') || {};
bottles[moduleId] = {
  ...bottles[moduleId],
  level_percent: payload.level_percent,
  level_ml: payload.level_ml,
  last_level_update: new Date().toISOString()
};

flow.set('bottles', bottles);

// Check for low level alerts
if (payload.level_percent < 20) {
  node.warn(`Low level alert: ${moduleId} at ${payload.level_percent}%`);

  // Send alert
  const alertMsg = {
    payload: {
      type: 'low_level_alert',
      module_id: moduleId,
      level: payload.level_percent,
      timestamp: new Date().toISOString()
    },
    topic: 'cocktail/alerts/low_level'
  };

  node.send([msg, alertMsg]);
  return;
}

return msg;

// Execute cocktail recipe
const recipe = msg.payload;
const bottles = flow.get('bottles') || {};

// Validate ingredients are available
for (const ingredient of recipe.ingredients) {
  const bottle = bottles[ingredient.bottle_id];
  if (!bottle || bottle.status !== 'online' || bottle.level_percent < 10) {
    msg.statusCode = 400;
    msg.payload = {
      error: 'Ingredient unavailable',
      ingredient: ingredient.bottle_id,
      bottle_status: bottle?.status || 'unknown'
    };
    return msg;
  }
}

// Calculate pump times based on amounts
const commands = [];
for (const ingredient of recipe.ingredients) {
  const pumpTimeMs = (ingredient.amount / 10) * 1000; // 10ml/second

  commands.push({
    topic: `cocktail/module/${ingredient.bottle_id}/pump/command`,
    payload: JSON.stringify({
      action: 'start',
      duration_ms: pumpTimeMs,
      speed: 255,
      recipe_id: recipe.id,
      timestamp: Date.now()
    })
  });
}

// Store recipe execution
const execution = {
  recipe_id: recipe.id,
  recipe_name: recipe.name,
  ingredients: recipe.ingredients,
  status: 'executing',
  start_time: new Date().toISOString(),
  commands: commands
};

flow.set('current_execution', execution);

// Send pump commands
msg.payload = {
  status: 'started',
  recipe: recipe.name,
  estimated_time: Math.max(...commands.map(c => JSON.parse(c.payload).duration_ms)) / 1000,
  execution_id: execution.start_time
};

// Send commands to MQTT
for (const cmd of commands) {
  node.send([null, cmd]);
}

return msg;

// Check for available updates
const fs = require('fs');
const https = require('https');

// Read current version
let currentVersion = '1.0.0';
try {
  const versionFile = fs.readFileSync('/opt/webroot/VERSION', 'utf8');
  currentVersion = versionFile.trim();
} catch (err) {
  node.warn('Could not read current version');
}

// Check remote versions
const options = {
  hostname: 'raw.githubusercontent.com',
  path: '/sebastienlepoder/cocktail-machine-prod/main/web/versions.json',
  method: 'GET'
};

const req = https.request(options, (res) => {
  let data = '';
  res.on('data', (chunk) => { data += chunk; });
  res.on('end', () => {
    try {
      const versions = JSON.parse(data);
      const latestVersion = versions.dashboard.latest;
      const hasUpdate = currentVersion !== latestVersion;

      msg.payload = {
        current_version: currentVersion,
        latest_version: latestVersion,
        update_available: hasUpdate,
        release_notes: versions.dashboard.notes || [],
        last_check: new Date().toISOString()
      };

      // Store update info in context
      flow.set('update_status', msg.payload);

      node.send(msg);
    } catch (err) {
      msg.payload = {
        error: 'Failed to check for updates',
        current_version: currentVersion,
        last_check: new Date().toISOString()
      };
      node.send(msg);
    }
  });
});

req.on('error', (err) => {
  msg.payload = {
    error: 'Network error checking updates',
    current_version: currentVersion,
    last_check: new Date().toISOString()
  };
  node.send(msg);
});

req.end();

return null; // Async response

// Trigger system update script
const { exec } = require('child_process');

msg.payload = {
  status: 'starting',
  message: 'Update process initiated',
  timestamp: new Date().toISOString()
};

// Run the update script in background
exec('sudo /opt/scripts/update_dashboard.sh', (error, stdout, stderr) => {
  if (error) {
    node.error(`Update failed: ${error.message}`);
    return;
  }

  node.log('Update process completed');
  node.log(`stdout: ${stdout}`);
  if (stderr) node.warn(`stderr: ${stderr}`);
});

return msg;

// Generate bottles status HTML
const bottles = flow.get('bottles') || {};

let html = '<h3>üçπ Bottle Status</h3>';

if (Object.keys(bottles).length === 0) {
  html += '<p>No bottles detected yet...</p>';
} else {
  html += '<div style="display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: 10px;">';

  Object.entries(bottles).forEach(([id, bottle]) => {
    const statusColor = bottle.status === 'online' ? 'green' : 'red';
    const levelColor = bottle.level_percent > 50 ? 'green' : bottle.level_percent > 20 ? 'orange' : 'red';

    html += `
      <div style="border: 1px solid #ddd; padding: 10px; border-radius: 5px;">
        <h4>${id}</h4>
        <p>Status: <span style="color: ${statusColor}">‚óè ${bottle.status || 'unknown'}</span></p>
        <p>Level: <span style="color: ${levelColor}">${bottle.level_percent || 0}%</span></p>
        <p>WiFi: ${bottle.wifi_connected ? '‚úÖ' : '‚ùå'}</p>
        <p>MQTT: ${bottle.mqtt_connected ? '‚úÖ' : '‚ùå'}</p>
        <p>IP: ${bottle.ip_address || 'Unknown'}</p>
        <small>Last seen: ${bottle.last_seen ? new Date(bottle.last_seen).toLocaleString() : 'Never'}</small>
      </div>
    `;
  });

  html += '</div>';
}

msg.payload = html;
return msg;

// Format update status for UI
const updateStatus = flow.get('update_status') || {
  current_version: 'Unknown',
  update_available: false,
  last_check: 'Never'
};

let html = '<h4>üîÑ System Updates</h4>';
html += `<p><strong>Current Version:</strong> ${updateStatus.current_version}</p>`;

if (updateStatus.update_available) {
  html += `<p style="color: orange;">üì¶ Update Available: ${updateStatus.latest_version}</p>`;
  if (updateStatus.release_notes && updateStatus.release_notes.length > 0) {
    html += '<p><strong>Release Notes:</strong></p><ul>';
    updateStatus.release_notes.slice(0, 3).forEach(note => {
      html += `<li>${note}</li>`;
    });
    html += '</ul>';
  }
} else {
  html += '<p style="color: green;">‚úÖ System is up to date</p>';
}

html += `<p><small>Last checked: ${updateStatus.last_check ? new Date(updateStatus.last_check).toLocaleString() : 'Never'}</small></p>`;

msg.payload = html;
return msg;
